			$this->alreadyInValidation = true;
			$retval = null;

			$failureMap = array();


			// We call the validate method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aFeaturevectorRelatedByTrackAId !== null) {
				if (!$this->aFeaturevectorRelatedByTrackAId->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aFeaturevectorRelatedByTrackAId->getValidationFailures());
				}
			}

			if ($this->aFeaturevectorRelatedByTrackBId !== null) {
				if (!$this->aFeaturevectorRelatedByTrackBId->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aFeaturevectorRelatedByTrackBId->getValidationFailures());
				}
			}

			if ($this->aDistancetype !== null) {
				if (!$this->aDistancetype->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aDistancetype->getValidationFailures());
				}
			}


			if (($retval = DistancePeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}



			$this->alreadyInValidation = false;
		}

		return (!empty($failureMap) ? $failureMap : true);
