		if ($this->isColumnModified(DistancePeer::TRACK_B_ID)) $criteria->add(DistancePeer::TRACK_B_ID, $this->track_b_id);
		if ($this->isColumnModified(DistancePeer::FEATUREVECTORTYPE_ID)) $criteria->add(DistancePeer::FEATUREVECTORTYPE_ID, $this->featurevectortype_id);
		if ($this->isColumnModified(DistancePeer::DISTANCETYPE_ID)) $criteria->add(DistancePeer::DISTANCETYPE_ID, $this->distancetype_id);
		if ($this->isColumnModified(DistancePeer::VALUE)) $criteria->add(DistancePeer::VALUE, $this->value);
		if ($this->isColumnModified(DistancePeer::INSERTED)) $criteria->add(DistancePeer::INSERTED, $this->inserted);
