		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseDistancePeer', $criteria, $con);
		}


		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = DistancePeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = DistancePeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = DistancePeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				DistancePeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Distancetype rows

				$key2 = DistancetypePeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = DistancetypePeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$cls = DistancetypePeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					DistancetypePeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Distance) to the collection in $obj2 (Distancetype)
				$obj2->addDistance($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


	/**
	 * Selects a collection of Distance objects pre-filled with all related objects except Distancetype.
	 *
	 * @param      Criteria  $criteria
	 * @param      PropelPDO $con
	 * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
	 * @return     array Array of Distance objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAllExceptDistancetype(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		// $criteria->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		DistancePeer::addSelectColumns($criteria);
		$startcol2 = (DistancePeer::NUM_COLUMNS - DistancePeer::NUM_LAZY_LOAD_COLUMNS);

		FeaturevectorPeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + (FeaturevectorPeer::NUM_COLUMNS - FeaturevectorPeer::NUM_LAZY_LOAD_COLUMNS);

		FeaturevectorPeer::addSelectColumns($criteria);
		$startcol4 = $startcol3 + (FeaturevectorPeer::NUM_COLUMNS - FeaturevectorPeer::NUM_LAZY_LOAD_COLUMNS);

		$criteria->addJoin(DistancePeer::TRACK_A_ID, FeaturevectorPeer::TRACK_ID, $join_behavior);

		$criteria->addJoin(DistancePeer::TRACK_B_ID, FeaturevectorPeer::TRACK_ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseDistancePeer', $criteria, $con);
		}


		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = DistancePeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = DistancePeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = DistancePeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				DistancePeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Featurevector rows

				$key2 = FeaturevectorPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = FeaturevectorPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$cls = FeaturevectorPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					FeaturevectorPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Distance) to the collection in $obj2 (Featurevector)
				$obj2->addDistanceRelatedByTrackAId($obj1);

			} // if joined row is not null

				// Add objects for joined Featurevector rows

				$key3 = FeaturevectorPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = FeaturevectorPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$cls = FeaturevectorPeer::getOMClass(false);

					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					FeaturevectorPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Distance) to the collection in $obj3 (Featurevector)
				$obj3->addDistanceRelatedByTrackBId($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

