	/**
	 * Method perform an INSERT on the database, given a Distance or Criteria object.
	 *
	 * @param      mixed $values Criteria or Distance object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseDistancePeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseDistancePeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(DistancePeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Distance object
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseDistancePeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseDistancePeer', $values, $con, $pk);
    }

		return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a Distance or Criteria object.
	 *
	 * @param      mixed $values Criteria or Distance object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseDistancePeer:doUpdate:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseDistancePeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(DistancePeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(DistancePeer::TRACK_A_ID);
			$selectCriteria->add(DistancePeer::TRACK_A_ID, $criteria->remove(DistancePeer::TRACK_A_ID), $comparison);

			$comparison = $criteria->getComparison(DistancePeer::TRACK_B_ID);
			$selectCriteria->add(DistancePeer::TRACK_B_ID, $criteria->remove(DistancePeer::TRACK_B_ID), $comparison);

			$comparison = $criteria->getComparison(DistancePeer::FEATUREVECTORTYPE_ID);
			$selectCriteria->add(DistancePeer::FEATUREVECTORTYPE_ID, $criteria->remove(DistancePeer::FEATUREVECTORTYPE_ID), $comparison);

			$comparison = $criteria->getComparison(DistancePeer::DISTANCETYPE_ID);
			$selectCriteria->add(DistancePeer::DISTANCETYPE_ID, $criteria->remove(DistancePeer::DISTANCETYPE_ID), $comparison);

		} else { // $values is Distance object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseDistancePeer:doUpdate:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseDistancePeer', $values, $con, $ret);
    }

    return $ret;
	}

	/**
	 * Method to DELETE all rows from the distance table.
	 *
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll($con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(DistancePeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += BasePeer::doDeleteAll(DistancePeer::TABLE_NAME, $con);
			// Because this db requires some delete cascade/set null emulation, we have to
			// clear the cached instance *after* the emulation has happened (since
			// instances get re-added by the select statement contained therein).
			DistancePeer::clearInstancePool();
			DistancePeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Method perform a DELETE on the database, given a Distance or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Distance object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(DistancePeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			DistancePeer::clearInstancePool();
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Distance) { // it's a model object
			// invalidate the cache for this single object
			DistancePeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else { // it's a primary key, or an array of pks
			$criteria = new Criteria(self::DATABASE_NAME);
			// primary key is composite; we therefore, expect
			// the primary key passed to be an array of pkey values
			if (count($values) == count($values, COUNT_RECURSIVE)) {
				// array is not multi-dimensional
				$values = array($values);
			}
			foreach ($values as $value) {
				$criterion = $criteria->getNewCriterion(DistancePeer::TRACK_A_ID, $value[0]);
				$criterion->addAnd($criteria->getNewCriterion(DistancePeer::TRACK_B_ID, $value[1]));
				$criterion->addAnd($criteria->getNewCriterion(DistancePeer::FEATUREVECTORTYPE_ID, $value[2]));
				$criterion->addAnd($criteria->getNewCriterion(DistancePeer::DISTANCETYPE_ID, $value[3]));
				$criteria->addOr($criterion);
				// we can invalidate the cache for this single PK
				DistancePeer::removeInstanceFromPool($value);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			
			$affectedRows += BasePeer::doDelete($criteria, $con);
			DistancePeer::clearRelatedInstancePool();
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Validates all modified columns of given Distance object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      Distance $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(Distance $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(DistancePeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(DistancePeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		return BasePeer::doValidate(DistancePeer::DATABASE_NAME, DistancePeer::TABLE_NAME, $columns);
	}

	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $track_a_id
	 * @param      int $track_b_id
	 * @param      int $featurevectortype_id
	 * @param      int $distancetype_id
	 * @param      PropelPDO $con
	 * @return     Distance
	 */
	public static function retrieveByPK($track_a_id, $track_b_id, $featurevectortype_id, $distancetype_id, PropelPDO $con = null) {
		$key = serialize(array((string) $track_a_id, (string) $track_b_id, (string) $featurevectortype_id, (string) $distancetype_id));
 		if (null !== ($obj = DistancePeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(DistancePeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(DistancePeer::DATABASE_NAME);
		$criteria->add(DistancePeer::TRACK_A_ID, $track_a_id);
		$criteria->add(DistancePeer::TRACK_B_ID, $track_b_id);
		$criteria->add(DistancePeer::FEATUREVECTORTYPE_ID, $featurevectortype_id);
		$criteria->add(DistancePeer::DISTANCETYPE_ID, $distancetype_id);
		$v = DistancePeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
