		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = QueryCommentTrackPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = QueryCommentTrackPeer::getInstanceFromPool($key1))) {
