			} // if obj1 already loaded

			// Add objects for joined QueryComment rows

			$key2 = QueryCommentPeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = QueryCommentPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$cls = QueryCommentPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					QueryCommentPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (QueryCommentTrack) to the collection in $obj2 (QueryComment)
				$obj2->addQueryCommentTrack($obj1);
			} // if joined row not null

			$results[] = $obj1;
		}
